mapper.update:
update easybuy_product_category
name=#{name}, parentId=#{parentId}, type=#{type},
where id = #{id}

mapper.select:
select
id,name,parentId,type,iconClass from easybuy_product_category where id
=#{id}

select epc1.*,epc2.name as parentName from easybuy_product_category
epc1 LEFT JOIN easybuy_product_category epc2 ON epc1.parentId=epc2.id
where 1=1

	and epc1.name like CONCAT ('%',#{name},'%')


	and epc1.parentId=#{parentId}


	and epc1.type=#{type}


	limit #{startIndex},#{pageSize}

SELECT count(*) count FROM easybuy_product_category where 1=1

	and name like CONCAT ('%',#{name},'%')


	and parentId=#{parentId}
